mapper.select:
SELECT b.BOARD_ID, b.BOARD_TITLE, c.BOARD_CATEGORY_JP AS CATEGORY_NAME_JP, t.BOARD_TAG_JP AS TAG_NAME_JP, b.USER_ID, u.USER_NICKNAME, b.BOARD_VIEWS, b.BOARD_WRITE_DATE, (SELECT COUNT(*) FROM BOARD_LIKE bl WHERE bl.BOARD_ID = b.BOARD_ID) AS LIKE_COUNT, (SELECT COUNT(*) FROM BOARD_REPLY r WHERE r.BOARD_ID = b.BOARD_ID) AS REPLY_COUNT FROM INI_BOARD b LEFT JOIN BOARD_CATEGORY c ON b.BOARD_CATEGORY = c.BOARD_CATEGORY LEFT JOIN BOARD_TAG t ON b.BOARD_TAG = t.BOARD_TAG LEFT JOIN INI_USER u ON b.USER_ID = u.USER_ID
AND ( LOWER(b.BOARD_TITLE) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(b.BOARD_CONTENT) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(u.USER_NICKNAME) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(b.USER_ID) LIKE '%' || LOWER(#{keyword}) || '%' )
AND b.BOARD_CATEGORY = #{category}
AND b.BOARD_TAG = #{tag}
ORDER BY b.BOARD_WRITE_DATE DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*) FROM INI_BOARD b LEFT JOIN INI_USER u ON b.USER_ID = u.USER_ID
AND ( LOWER(b.BOARD_TITLE) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(b.BOARD_CONTENT) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(u.USER_NICKNAME) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(b.USER_ID) LIKE '%' || LOWER(#{keyword}) || '%' )
AND b.BOARD_CATEGORY = #{category}
AND b.BOARD_TAG = #{tag}
